css:
:root {
    margin: 15px;
}
.user-info {
    font-size:14px;
    padding: 2px;
}
.print-format {
    padding: 1rem;
    font-family: inherit;
    justify-content: space-between;
}
.print-format th {
    background-color: #fff!important;
}
.copy {
    flex:1;
}
.header {
    display: flex;
    justify-content: space-between;
    border-bottom: 1px solid black;
}
.logo {
    max-width: 125px;
    max-height: 75px;
}
.type {
    font-size: 1.3em;
}
.title {
    display: flex;
    flex-direction:column;
    font-size: 16px;
}
/*table*/
.content {
    border: 1px solid black;
    width:100%;
    margin-bottom:5px;
}
.tg  {
    border-collapse:collapse;
    border-spacing:0;

}
.tg td {
    overflow:hidden;
    word-break:normal;
    border: 1px solid;
    text-align:center;
}
.tg th {
    overflow:hidden;
    word-break:normal;
    border: 1px solid;
    text-align: center;
}
.tg-lvth {
    width:75%;
    position: relative;
    text-align: center;

}
.acc-response {
    flex:1;
}
.cus-response {
    flex:1;
}
.flex {
    display: flex;
    justify-content: space-between; 

}
.notes {
    font-size: 14px;
}
